/** Get data from multiple rows. If null or undefined is given, data from all rows is returned.
* @public
* @param {Array.<number>=} rowIndices Row indices as shown in the view
* @return {!Array.<Object>}
* @example
* grid.getMultipleRowData(); // all row data is returned
* grid.getMultipleRowData([0, 2, 4]); // Three row data is returned
*/
Grid_Grid.prototype.getMultipleRowData = function(rowIndices) {
	if(typeof rowIndices === "number") {
		return [this.getRowData(rowIndices)];
	}

	var rows, rowCount, r, rowDef;
	var rowDefs = this.getRowDefinitions();
	if(Array.isArray(rowIndices)) {
		rowCount = rowIndices.length;
		rows = new Array(rowCount);

		for(r = 0; r < rowCount; ++r) {
			rowDef = rowDefs[rowIndices[r]];
			rows[r] = rowDef ? rowDef.getRowData() : null;
		}
	} else {
		rowCount = rowDefs.length;
		rows = new Array(rowCount);

		for(r = 0; r < rowCount; ++r) {
			rowDef = rowDefs[r];
			rows[r] = rowDef ? rowDef.getRowData() : null;
		}
	}
	return rows;
};
